import { useEffect, useState } from "react";
import styled from "styled-components";

const TodoGrid = styled.section`
  display: grid;
  width: 100%;
  grid-template-columns: repeat(4, 1fr);
  gap: 10px;
`;

const TodoBox = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  min-height: 70px;
  max-height: 100px;
  border: 1px black solid;
  border-radius: 10px;
  background-color: white;
  overflow: auto;
`;

const TodoText = styled.div`
  text-align: center;
  background-color: white;
`;

const DelBtn = styled.button`
  position: relative;
  width: 40px;
  height: 25px;
  border-radius: 10px;
  text-align: center;
  background-color: #ffe0e6;
  color: #f32d67;
  right: -158px;
  bottom: -2px;
  :active {
    background-color: #efefef;
  }
`;

function Todo() {
  const [todos, setTodos] = useState([]);
  const [changeTodos, setChangeTodos] = useState([]);
  const [isEditable, setIsEditable] = useState(false);

  useEffect(() => {
    fetch("http://localhost:3001/todos", {
      method: "GET",
    })
      .then((res) => {
        if (!res.ok) {
          throw Error("위잉 위잉 에러에요");
        }
        return res.json();
      })
      .then((data) => {
        setTodos(data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  const handleDelBtn = (id) => {
    fetch("http://localhost:3001/todos/" + id, {
      method: "DELETE",
    }).then(window.location.reload());
  };
  // console.log(todos)

  const handleDoubleClick = () => {
    setIsEditable(true);
  };

  const onSubmit = (id) => {
    // fetch("http://localhost:3001/todos/" + id, {
    //     method: "PATCH",
    //     headers: {
    //         "Content-Type" : "application/json",
    //     },
    //     body: JSON.stringify({
    //         todo: changeTodos,
    //     })
    // })
    console.log(id)
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      setIsEditable(false);
    }
  };

  return (
    <TodoGrid>
      {todos.map((todo) => (
        <TodoBox key={todo.id}>
          {isEditable ? (
            <form onSubmit={() => onSubmit(todo.id)}>
              <input
                onChange={(e)=> setChangeTodos(e.target.value)}
                onKeyDown={handleKeyDown}
              />
            </form>
          ) : (
            <TodoText onDoubleClick={handleDoubleClick}>{todo.todo}</TodoText>
          )}
          <DelBtn onClick={() => handleDelBtn(todo.id)}>DEL</DelBtn>
        </TodoBox>
      ))}
      <input disabled value={"연습이다"} />
    </TodoGrid>
  );
}

export default Todo;
