import {atom, selector} from "recoil"


export const todoState = atom({
    key: "category",
    default: [],
    // effects_UNSTABLE: [] // 로컬스토리지
})


export const toDoSelector = selector({
    key: "toDoSelector",
    get: ({ get }) => {
        const toDos = get(todoState)
        return toDos
    }
})
